import React from 'react';
import Card from './Card';
import {useFetch, deleteStudent} from '../service/StudentService';
import {useState, useEffect} from 'react';


function FuncCardSet(props) {
    const {loading, data} = useFetch('getAll', null);

    console.log(data);
    
    handleSubmit=(event)=> {

       let ok=window.confirm("Are you sure you want to update this student?\nPress ok to continue, cancal to abort");
       if(ok==true){
         alert()
       }


        // do your call maybe to post this information or update something with an api

       // this.setState({color: ''});

        event.preventDefault();
    }

    const handleDelete=(id) =>{
        let ok=window.confirm('Are you sure you wish the delete this student? \nPress ok to confirm');
        if(ok==true){
            //visual 
            var RemoveCard=document.getElementById('card'+id);
            RemoveCard.parentNode.removeChild(RemoveCard);
            // Actual Axios call
            deleteStudent(id);
            window.alert('Student has been deleted')
        }
    }
    return(
        <div>
            {loading && <p>loading...</p>}
            {data && data.length > 0 && data.map(student => <Card key={student.id} info={student} handleDelete={handleDelete}/>)}
        </div>
    )
}

export default FuncCardSet;
