import React, {Component} from 'react';
import './Card.css';

class Card extends Component {

    render() {
        return (
            <div className="card" id={"card" + this.props.info.id}>
                <Header fname={this.props.info.firstname} lname={this.props.info.lastname} />
                <Body url={this.props.info.url} imgAlt={this.props.alt} email={this.props.info.email} department={this.props.info.department}/>
                <Footer details={this.props.details} handl/>

            </div>

        );
    }
}

function Header(props) {
    return (
        <div className="card-header">
            <strong>
            {props.fname}
            {props.lname}
            </strong>
        </div>
    );
}

function Body(props) {
    return(
        <div className="card-body">
            <img src={props.url} 
            alt="Student " />
            <div className="container">
                <p>Email: {props.email}</p>
                <p>Department: {props.department}</p>
            </div>
        </div>
    );
}

function Footer(props) {
    return(
        <div className="card-footer">
            <button className="btn btn-danger" onClick={props.handleDelete.bind(this, props.studentId)}>Delete</button>
                   </div>
    );
}

// exposes the Card to other models/files so it can imported and used there
export default Card;
