import React, {Component} from 'react';
import './Card.css';

class Card extends Component {

    render() {
        return (
            <div className="card" id={"card" + this.props.info.id}>
                <Header Fname="Student Card" lname={this.props.info} />
                <Body url={this.props.info.url} imgAlt={this.props.imgAlt} name={this.props.name} series={this.props.series}/>
                <Footer details={this.props.details} handleDelete={this.props.handleDelete} studentId={this.props.info.id}/>

            </div>

        );
    }
}

function Header(props) {
    return (
        <div className="card-header">
            {props.title}
        </div>
    );
}

function Body(props) {
    return(
        <div className="card-body">
            <img src={props.url} 
            alt={props.imgAlt} />
            <div className="container">
                <p>Name: {props.name}</p>
                <p>Series: {props.series}</p>
            </div>
        </div>
    );
}

function Footer(props) {
    return(
        <div className="card-footer">
            <button className="btn btn-danger" onClick={props.handleDelete.bind(this, props.studentId)}>Delete</button>
                   </div>
    );
}

// exposes the Card to other models/files so it can imported and used there
export default Card;
