import React, {Component} from 'react';
import './Card.css';

class Card extends Component {

    render() {
        return (
            <div className="card" id={"card" + this.props.info.id}>
                <Header fname={this.props.info.firstname} lname={" "+ this.props.info.lastname} />
                <Body url={this.props.info.url} imgAlt={this.props.alt} email={this.props.info.email} department={this.props.info.department}/>
                <Footer handleDelete={this.props.handleDelete} studentId={this.props.info.id}/>

            </div>

        );
    }
}

function Header(props) {
    return (
        <div className="card-header">
            <strong>
            {props.fname}
            {props.lname}
            </strong>
        </div>
    );
}

function Body(props) {
    return(
        <div className="card-body">
            <img src={props.url} 
            alt="Student " />
            <div className="container">
                <p>Email: {props.email}</p>
                <p>Department: {props.department}</p>
            </div>
        </div>
    );
}

function Footer(props) {
    return(
        <div className="card-footer">
            <button className="btn btn-danger" onClick={props.handleDelete.bind(this, props.studentId)}>Delete</button>
            <button className="btn btn-warning" onClick={props.handleDelete.bind(this,props.studentId)}>Update</button>
                   </div>
    );
}

function Modal(props){
    
<div className="form-style">
                <h3 style={ {color: this.state.color} } >Simple Form</h3>
                <form onSubmit={this.handleSubmit} >
                    <div className="form-group">
                        <label>Favorite Color</label>
                        <input type="text" value={this.state.color} onChange={this.handleChange} className="form-control"/>
                        <p>Value: {this.state.color}</p>
                    </div>

                    <input type="submit" value="Submit" className="btn btn-primary"/>
                </form>
            </div>
}
    
    

// exposes the Card to other models/files so it can imported and used there
export default Card;
